import React from 'react';
import './App.css';
import Item from './components/Item'




function App () {


  let db 


    let urlIDF = 'https://rickandmortyapi.com/api/character'
    fetch(urlIDF)
      .then(function (response) {
        return response.json()
      })
      .then((data) =>  dataToVar(data))
      .catch(err => alert(err))


      function dataToVar(data){        

        db=data
      }




    return (
      <div className="App">
        <Item />
        <span>{db.results[0].name}</span>
      </div>
    );

}

export default App;
