import React, { useEffect, useState } from 'react';
import './App.css';
import Item from './components/Item'




function App() {

  let [database, setDatabase] = useState([])
  let firstURL = 'https://rickandmortyapi.com/api/character/?page=1'



  const fectchAPI = (url) => {
    fetch(url)
      .then(function (response) {
        return response.json()
      })
      .then((data) => { setDatabase(data.results) })
      .catch(err => alert(err))

  }


  useEffect(() => {
    fectchAPI(firstURL)
  }, []
  )

  console.log(database)


  return (
    <div className="App">
      {
        database.map((item, index) => {
          return (
            
            <Item  name={item.name} status={item.status} species={item.species} image={item.image} />
          )
        })
      }
    </div>



  );

}

export default App;
