import React from 'react';
import './App.css';
import Item from './components/Item'




function App () {


  let data2
  let strItem =""


    let urlIDF = 'https://rickandmortyapi.com/api/character'
    fetch(urlIDF)
      .then(function (response) {
        return response.json()
      })
      .then((data) =>  itemDefinition(data, 0))
      .catch(err => alert(err))



    function itemDefinition(data, i){
        debugger

        for (let index = i ; index < i+3; i++) {
          strItem += `<Item image="data.results[${i}].image" name="data.results[${i}].name" status="data.results[${i}].status" species="data.results[${i}].species" />`

        }

      return strItem
    }


    return (
      <div className="App">
        {strItem}
      </div>
    );

}

export default App;
