import React, { useEffect, useState } from 'react';
import './App.css';
import Item from './components/Item'




function App() {
let databaseView =[]
  let [database, setDatabase] = useState([])
  let [lastIndex, setLastIndex] = useState(2)
  let firstURL = 'https://rickandmortyapi.com/api/character'



  const fectchAPI = (url) => {
    fetch(url)
      .then(function (response) {
        return response.json()
      })
      .then((data) => { setDatabase(data.results) })
      .catch(err => alert(err))

  }


  useEffect(() => {
    fectchAPI(firstURL)
  }, []
  )

  const seeMoreFunction = ()=>{
      setLastIndex = lastIndex+3
  }

  databaseView = database.slice(0,lastIndex+1)
  console.log(databaseView)


  return (
    <div className="App">

    <div className="App-items">
      {
        databaseView.map((item, index) => {
          return (
            
            <Item  name={item.name} status={item.status} species={item.species} image={item.image} />
          )
        })
      }
    </div>
    <button className="btn-seeMore" onClick={seeMoreFunction}>See More</button>
    </div>

  );
}

export default App;
